<script setup>
import { ref } from 'vue'
import { useRouter } from 'vue-router'

const router = useRouter()
const props = defineProps({
    amount: {
        type: Number,
        default: 0
    }
})

const paymentMethod = ref("银行卡")

const bankCard = ref({
    cardNumber: '',
    cardName: '',
    cardExpiry: '',
    cardCVV: ''
})


const resetPayment = () => {
    paymentMethod.value = "银行卡"
    bankCard.value = {
        cardNumber: '',
        cardName: '',
        cardExpiry: '',
        cardCVV: ''
    }
}

const confirmPayment = () => {
    router.push('/paymentSuccess')
}

</script>

